import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import { BASE_API_URL, APP_NAME } from "./globals";

const EditMember = () => {
    const [id_mem, setId] = useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [sex, setSex] = useState("");
    const [birthday, setBirthday] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");
    const [zipcode, setZipcode] = useState("");
    const [country, setCountry] = useState("");

    const location = useLocation();
    const navigate = useNavigate();
    const { id } = location.state || {};

    const fetchMember = async () => {
        try {
            const response = await axios.post(
                BASE_API_URL + "/members/searchid",
                {
                    id_mem: id,
                },
            );
            setId(response.data.user.id_mem);
            setName(response.data.user.name_mem);
            setEmail(response.data.user.email_mem);
            setPassword(response.data.user.password_mem);
            setSex(response.data.user.sex_mem);
            setBirthday(response.data.user.birthday_mem);
            setPhone(response.data.user.phone_mem);
            setAddress(response.data.user.address_mem);
            setZipcode(response.data.user.zipcode_mem);
            setCountry(response.data.user.country_mem);
        } catch (error) {
            alert("Failed to fetch member data");
        }
    };

    useEffect(() => {
        fetchMember(); // Trigger search when the component mounts
    }, [id]);

    const handleEdit = async (e) => {
        e.preventDefault();
        const userConfirmed = window.confirm("ยืนยันการบันทึกข้อมูล");
        if (userConfirmed) {
            try {
                await axios.put(BASE_API_URL + "/membersedit", {
                    id_mem: id_mem,
                    name_mem: name,
                    email_mem: email,
                    password_mem: password,
                    sex_mem: sex,
                    birthday_mem: birthday
                        ? `${birthday.getFullYear()}-${(birthday.getMonth() + 1)
                              .toString()
                              .padStart(2, "0")}-${birthday
                              .getDate()
                              .toString()
                              .padStart(2, "0")}`
                        : null,
                    phone_mem: phone,
                    address_mem: address,
                    zipcode_mem: zipcode,
                    country_mem: country,
                });
                alert("Member Edited successfully");
                navigate("/search");
            } catch (error) {
                alert("Failed to edit member");
            }
        }
    };

    const handledel = async () => {
        const userConfirmed = window.confirm("ยืนยันการลบข้อมูล");
        if (userConfirmed) {
            try {
                await axios.delete(BASE_API_URL + "/membersdel", {
                    data: { id_mem },
                });
                alert("Member Deleted successfully");
                navigate("/search");
            } catch (error) {
                alert("Failed to Delete member data");
            }
        }
    };

    const handlecancel = () => {
        navigate("/search");
    };

    return (
        <div>
            <h2>Edit Member</h2>
            <form onSubmit={handleEdit}>
                <div>
                    <input
                        type="hidden"
                        value={id_mem}
                        onChange={(e) => setId(e.target.value)}
                    />
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        placeholder="ponglert.s@ubru.ac.th"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="text"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Sex:</label>
                    <select
                        value={sex}
                        onChange={(e) => setSex(e.target.value)}
                    >
                        <option value="1">ชาย</option>
                        <option value="2">หญิง</option>
                    </select>
                </div>
                <div>
                    <label>Birthday:</label>
                    <DatePicker
                        selected={birthday}
                        onChange={(date) => setBirthday(date)}
                        dateFormat="yyyy-MM-dd"
                        isClearable
                        placeholderText="Select a date"
                        required
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Address:</label>
                    <textarea
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        rows={5}
                        required
                    />
                </div>
                <div>
                    <label>Zipcode:</label>
                    <input
                        type="text"
                        value={zipcode}
                        onChange={(e) => setZipcode(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Country:</label>
                    <input
                        type="text"
                        value={country}
                        onChange={(e) => setCountry(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Save</button>
            </form>
            <button onClick={() => handledel()}>Delete</button>
            <button onClick={() => handlecancel()}>Cancel</button>
        </div>
    );
};

export default EditMember;
