import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// eslint-disable-next-line
import { BASE_API_URL, APP_NAME } from "./globals";
const MemberInsert = () => {
const [name, setName] = useState("");
const [email, setEmail] = useState("");
const [password, setPassword] = useState("");
const [sex, setSex] = useState("");
const [birthday, setBirthday] = useState("");
const [phone, setPhone] = useState("");
const [address, setAddress] = useState("");
const [zipcode, setZipcode] = useState("");
const [country, setCountry] = useState("");
const navigate = useNavigate();
const handleAdd = async (e) => {
e.preventDefault();
const userConfirmed = window.confirm("ยืนยันการบันทึกข้อมูล");
if (userConfirmed) {
try {
await axios.post(BASE_API_URL + "/membersadd", {
name_mem: name,
email_mem: email,
password_mem: password,
sex_mem: sex,
birthday_mem: birthday,
phone_mem: phone,
address_mem: address,
zipcode_mem: zipcode,
country_mem: country,
});
alert("Member added successfully");
navigate("/search");
} catch (error) {
alert("Failed to add member");
}
}
};
const handlecancel = () => {
navigate("/search");
};
return (
<div>
<h2>Add Member</h2>
<form onSubmit={handleAdd}>
<div>
<label>Name:</label>
<input
type="text"
value={name}
onChange={(e) => setName(e.target.value)}
required
/>
</div>
<div>
<label>Email:</label>
<input
type="email"
placeholder="ponglert.s@ubru.ac.th"
value={email}
onChange={(e) => setEmail(e.target.value)}
required
/>
</div>
<div>
<label>Password:</label>
<input
type="text"
value={password}
onChange={(e) => setPassword(e.target.value)}
required
/>
</div>
<div>
<label>Sex:</label>
<select value={sex} onChange={(e) => setSex(e.target.value)}>
<option value="1">ชาย</option>
<option value="2">หญิง</option>
</select>
</div>
<div>
<label>Birthday:</label>
<input
type="date"
value={birthday}
onChange={(e) => setBirthday(e.target.value)}
required
/>
</div>
<div>
<label>Phone:</label>
<input
type="text"
value={phone}
onChange={(e) => setPhone(e.target.value)}
required
/>
</div>
<div>
<label>Address:</label>
<textarea
type="text"
value={address}
onChange={(e) => setAddress(e.target.value)}
rows="5"
required
/>
</div>
<div>
<label>Zipcode:</label>
<input
type="text"
value={zipcode}
onChange={(e) => setZipcode(e.target.value)}
required
/>
</div>
<div>
<label>Country:</label>
<input
type="text"
value={country}
onChange={(e) => setCountry(e.target.value)}
required
/>
</div>
<button type="submit">Save</button>
</form>
<button onClick={() => handlecancel()}>Cancel</button>
</div>
);
};
export default MemberInsert;
