const handleEdit = async (e) => {
    e.preventDefault();
    if (!window.confirm("ยืนยันการบันทึกข้อมูล?")) return;
    setLoading(true);
    try {
        console.log(" CALL API membersEdit");
        console.log(" id_mem = ",id_mem);
        const idAsInteger = parseInt(id_mem, 10);
        console.log("* idAsInteger = ",idAsInteger);

        // ตรวจสอบข้อมูลที่ส่งไป
        const response = await axios.put(`${BASE_API_URL}/membersedit`, {
            id_mem:idAsInteger,
            name_mem: name,
            email_mem: email,
            password_mem: password,
            sex_mem: sex,
            birthday_mem: birthday ? birthday.toISOString().split("T")[0] : null,
            phone_mem: phone,
            address_mem: address,
            zipcode_mem: zipcode,
            country_mem: country,
        });

        // ตรวจสอบผลลัพธ์ที่ได้จากการส่งข้อมูล
        console.log(response.data);
        if (response.status === 200) {
            alert("บันทึกข้อมูลเรียบร้อย");
            navigate("/search");
        } else {
            alert("เกิดข้อผิดพลาดในการบันทึกข้อมูล");
        }
    } catch (error) {
        console.error("Error updating member:", error);
        alert("ไม่สามารถบันทึกข้อมูลได้");
    } finally {
        setLoading(false);
    }
};
