import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { BASE_API_URL } from "./globals";

const MemberEdit = () => {
  const [id_mem, setId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [sex, setSex] = useState("1");
  const [birthday, setBirthday] = useState(null);
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [zipcode, setZipcode] = useState("");
  const [country, setCountry] = useState("");
  const [loading, setLoading] = useState(false);

  const location = useLocation();
  const navigate = useNavigate();
  const { id: paramId } = useParams();
  const id = location.state?.id || paramId;

  const fetchMember = useCallback(async () => {
    if (!id) return;
    setLoading(true);
    try {
      const { data } = await axios.get(`${BASE_API_URL}/members/searchid`, { params: { id_mem: id } });
      const user = data?.user || {};
      setId(user.id_mem || "");
      setName(user.name_mem || "");
      setEmail(user.email_mem || "");
      setPassword(user.password_mem || "");
      setSex(user.sex_mem || "1");
      setBirthday(user.birthday_mem ? new Date(user.birthday_mem) : null);
      setPhone(user.phone_mem || "");
      setAddress(user.address_mem || "");
      setZipcode(user.zipcode_mem || "");
      setCountry(user.country_mem || "");
    } catch (error) {
      console.error("Error fetching member:", error);
      alert("ไม่สามารถโหลดข้อมูลสมาชิกได้");
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchMember();
  }, [fetchMember]);

  const handleEdit = async (e) => {
    e.preventDefault();
    if (!window.confirm("ยืนยันการบันทึกข้อมูล?")) return;
    setLoading(true);
    try {
      await axios.put(`${BASE_API_URL}/membersedit`, {
        id_mem,
        name_mem: name,
        email_mem: email,
        password_mem: password,
        sex_mem: sex,
        birthday_mem: birthday ? birthday.toISOString().split("T")[0] : null,
        phone_mem: phone,
        address_mem: address,
        zipcode_mem: zipcode,
        country_mem: country,
      });
      alert("บันทึกข้อมูลเรียบร้อย");
      navigate("/search");
    } catch (error) {
      console.error("Error updating member:", error);
      alert("ไม่สามารถบันทึกข้อมูลได้");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm("ยืนยันการลบข้อมูล?")) return;
    setLoading(true);
    try {
      await axios.delete(`${BASE_API_URL}/membersdel`, { params: { id_mem } });
      alert("ลบข้อมูลเรียบร้อย");
      navigate("/search");
    } catch (error) {
      console.error("Error deleting member:", error);
      alert("ไม่สามารถลบข้อมูลได้");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>แก้ไขข้อมูลสมาชิก</h2>
      {loading ? <p>กำลังโหลด...</p> : (
        <form onSubmit={handleEdit}>
          <input type="hidden" value={id_mem} readOnly />
          <label>ชื่อ:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
          <label>อีเมล:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <label>รหัสผ่าน:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <label>เพศ:</label>
          <select value={sex} onChange={(e) => setSex(e.target.value)}>
            <option value="1">ชาย</option>
            <option value="2">หญิง</option>
          </select>
          <label>วันเกิด:</label>
          <DatePicker selected={birthday} onChange={setBirthday} dateFormat="yyyy-MM-dd" isClearable />
          <label>โทรศัพท์:</label>
          <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} required />
          <label>ที่อยู่:</label>
          <textarea value={address} onChange={(e) => setAddress(e.target.value)} rows="5" required />
          <label>รหัสไปรษณีย์:</label>
          <input type="text" value={zipcode} onChange={(e) => setZipcode(e.target.value)} required />
          <label>ประเทศ:</label>
          <input type="text" value={country} onChange={(e) => setCountry(e.target.value)} required />
          <button type="submit" disabled={loading}>บันทึก</button>
        </form>
      )}
      <button onClick={handleDelete} disabled={loading}>ลบ</button>
      <button onClick={() => navigate("/search")} disabled={loading}>ยกเลิก</button>
    </div>
  );
};

export default MemberEdit;
