import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { BASE_API_URL } from "./globals";
import "./MemberEdit.css";

const MemberEdit = () => {
  const [id_mem, setId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [sex, setSex] = useState("");
  const [birthday, setBirthday] = useState(null);
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [zipcode, setZipcode] = useState("");
  const [country, setCountry] = useState("");

  const location = useLocation();
  const navigate = useNavigate();
  const { id } = location.state || {};

  useEffect(() => {
    if (id) {
      fetchMember();
    }
  }, [id]);

  const fetchMember = async () => {
    try {
      const response = await axios.post(`${BASE_API_URL}/members/searchid`, { id_mem: id });
      if (response.data && response.data.user) {
        const user = response.data.user;
        setId(user.id_mem || "");
        setName(user.name_mem || "");
        setEmail(user.email_mem || "");
        setPassword(user.password_mem || "");
        setSex(user.sex_mem || "");
        setBirthday(user.birthday_mem ? new Date(user.birthday_mem) : null);
        setPhone(user.phone_mem || "");
        setAddress(user.address_mem || "");
        setZipcode(user.zipcode_mem || "");
        setCountry(user.country_mem || "");
      }
    } catch (error) {
      alert("ไม่สามารถดึงข้อมูลสมาชิกได้");
    }
  };

  const handleEdit = async (e) => {
    e.preventDefault();
    const userConfirmed = window.confirm("ยืนยันการบันทึกข้อมูล");
    if (userConfirmed) {
      try {
        const formattedBirthday = birthday
          ? `${birthday.getFullYear()}-${(birthday.getMonth() + 1).toString().padStart(2, "0")}-${birthday.getDate().toString().padStart(2, "0")}`
          : null;

        await axios.put(`${BASE_API_URL}/membersedit`, {
          id_mem,
          name_mem: name,
          email_mem: email,
          password_mem: password,
          sex_mem: sex,
          birthday_mem: formattedBirthday,
          phone_mem: phone,
          address_mem: address,
          zipcode_mem: zipcode,
          country_mem: country,
        });

        alert("แก้ไขข้อมูลสมาชิกเรียบร้อยแล้ว");
        navigate("/search");
      } catch (error) {
        alert("ไม่สามารถแก้ไขข้อมูลสมาชิกได้");
      }
    }
  };

  const handleDelete = async () => {
    const userConfirmed = window.confirm("คุณต้องการลบสมาชิกนี้หรือไม่?");
    if (userConfirmed) {
      try {
        await axios.delete(`${BASE_API_URL}/members/delete`, {
          data: { id_mem },
        });

        alert("ลบสมาชิกเรียบร้อยแล้ว");
        navigate("/search");
      } catch (error) {
        alert("ไม่สามารถลบสมาชิกได้");
      }
    }
  };

  return (
    <div className="member-edit-container">
      <h2>แก้ไขข้อมูลสมาชิก</h2>
      <form onSubmit={handleEdit} className="member-form">
        <input type="hidden" value={id_mem} readOnly />
        <label>ชื่อ:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        <label>อีเมล:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <label>รหัสผ่าน:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <label>เพศ:</label>
        <select value={sex} onChange={(e) => setSex(e.target.value)}>
          <option value="1">ชาย</option>
          <option value="2">หญิง</option>
        </select>
        <label>วันเกิด:</label>
        <DatePicker selected={birthday} onChange={(date) => setBirthday(date)} dateFormat="yyyy-MM-dd" isClearable />
        <label>เบอร์โทร:</label>
        <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} required />
        <label>ที่อยู่:</label>
        <textarea value={address} onChange={(e) => setAddress(e.target.value)} rows="5" required />
        <label>รหัสไปรษณีย์:</label>
        <input type="text" value={zipcode} onChange={(e) => setZipcode(e.target.value)} required />
        <label>ประเทศ:</label>
        <input type="text" value={country} onChange={(e) => setCountry(e.target.value)} required />
        
        <button type="submit" className="save-button">บันทึก</button>
        <button type="button" onClick={() => navigate("/search")} className="cancel-button">ยกเลิก</button>
        <button type="button" onClick={handleDelete} className="delete-button">ลบ</button>
      </form>
    </div>
  );
};
const handleDelete = async () => {
  const userConfirmed = window.confirm("คุณต้องการลบสมาชิกนี้หรือไม่?");
  if (userConfirmed) {
    try {
      await axios.post(`${BASE_API_URL}/members/delete`, { id_mem });  // เปลี่ยนเป็น post
      alert("ลบสมาชิกเรียบร้อยแล้ว");
      navigate("/search");
    } catch (error) {
      alert(`ไม่สามารถลบสมาชิกได้: ${error.response?.data?.message || error.message}`);
    }
  }
};


export default MemberEdit;
